def update_lang(lang):
    try:
        path = os.path.join(base_dir, f"ui_{lang}.json")
        with open(path, 'r', encoding='utf-8') as f:
            data = json.load(f)
        new = sum(1 for k in translations[lang] if k not in data)
        data.update(translations[lang])
        with open(path, 'w', encoding='utf-8') as f:
            json.dump(data, f, ensure_ascii=False, indent=2)
        print(f"[OK] {lang}: +{new} keys")
        return new
    except Exception as e:
        print(f"[ERR] {lang}: {e}")
        return 0

total = sum(update_lang(lang) for lang in translations)
print(f"\n[DONE] Total: +{total} translations")
