def update_file(lang):
    file_path = os.path.join(base_dir, f"ui_{lang}.json")
    if not os.path.exists(file_path):
        print(f"[X] {lang}: File not found")
        return 0
    try:
        with open(file_path, 'r', encoding='utf-8') as f:
            data = json.load(f)
        new = sum(1 for k in all_translations[lang] if k not in data)
        data.update(all_translations[lang])
        with open(file_path, 'w', encoding='utf-8') as f:
            json.dump(data, f, ensure_ascii=False, indent=2)
        print(f"[OK] {lang}: +{new} keys")
        return new
    except Exception as e:
        print(f"[ERR] {lang}: {e}")
        return 0

total = 0
for lang in all_translations:
    total += update_file(lang)
print(f"\n[DONE] Total: +{total} translations")
