template_esp32:
void setup() {
  pinMode(2, OUTPUT); // 内置 LED
  Serial.begin(115200);
  Serial.println("ESP32 start!");
}

void loop() {
  digitalWrite(2, HIGH);
  delay(1000);
  digitalWrite(2, LOW);
  delay(1000);
}

